import React,{useEffect,useState} from 'react';

const Timer = (props) => {
  const [timerHour,setTimerHour] = useState(1);
  const [timerMin,setTimerMin] = useState(0);
  const [timerSec,setTimerSec] = useState(10);

  // const inret = setInterval(()=>{
  //   if(timerSec != 0){
  //     timerSec = timerSec - 1;
  //   }else{
  //     timerSec = 60;
  //     timerMin = timerMin - 1;
  //   }
  // },1000)
  React.useEffect(() => {
    if(timerHour == 0 && timerMin == 0 && timerSec == 0){
      alert("End")
    }else{
      if(timerSec !== 0 ){
        const timer =
          timerSec > 0 && setInterval(() => setTimerSec(timerSec - 1), 1000);
          return () => clearInterval(timer);
      }else if(timerMin !== 0 && timerHour > 0 ){
        setTimerMin(timerMin - 1);
        setTimerSec(60)
      }else if(timerHour !== 0 && timerMin == 0){
        setTimerHour(timerHour - 1);
        setTimerMin(60)
      }
    }
    }, [timerSec]);

  return (
    <div>
      {`${timerHour}:${timerMin}:${timerSec}`}
    </div>
  )
}

export default Timer
