import * as axios from "axios";
import React, {useEffect,useState} from 'react';
import ModalWindow from './ModalWindow';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'

function App() {
  const [date,setDate] = useState(new Date());
  const [styleTime,setStyleTime] = useState("am");
  const [modal,setModal] = useState(true);
  const [location, setLocation] = useState("")
  const [colors, setColors] = useState(stateColors)
  useEffect(() => {
     setInterval(() => setDate(new Date()), 1000);
   }, []);
   const clickBtn = ()=>{
     setModal(true)
   }
   const clickCloseModal = ()=>{
     setModal(false)
   }
   const choiceStyleTime = (styleTime)=>{
     setStyleTime(styleTime)
   }
   useEffect(()=>{
     fetch('/api')
     .then(response => response.json())
     .then(response => setLocation(`${response.region}/ ${response.country}`))
   }, [])

  return(
       <ModalWindow
         show={modal} onHide={clickCloseModal}
         timeAm={date.toLocaleTimeString()} timePm={`${date.getHours() % 12 || 12}:${date.getMinutes()}:${date.getSeconds()}`}
         choiceStyleTime={choiceStyleTime}
       />
  )
}
