import * as axios from "axios";
import React, {useEffect,useState} from 'react';
import './App.css'

function App() {
  const [date,setDate] = useState(new Date());
  const [styleTime,setStyleTime] = useState("am");
  const [modal,setModal] = useState(false);
  const [location, setLocation] = useState("")
  useEffect(() => {
     setInterval(() => setDate(new Date()), 1000);
   }, []);
   const clickBtn = ()=>{
     setModal(true)
   }
   useEffect(()=>{
     fetch('/api')
     .then(response => response.json())
     .then(response => setLocation(`${response.region}/ ${response.country}`))
   }, [])

  return(
    <div className="main-page">
      <div className="header-btn">
        <p onClick={clickBtn}>&#9776;</p>
      </div>
      <div className="realTime-box">
        <p className="time">{styleTime == "pm" ? `${date.getHours() % 12 || 12}:${date.getMinutes()}:${date.getSeconds()}` : date.toLocaleTimeString()}</p>
      </div>
      {location !== "" ? <div className="location"><p >{location}</p></div> : <div></div>}


    </div>

  )
}

export default App;
