import * as axios from "axios";
import React, {useEffect,useState} from 'react';
import ModalWindow from './ModalWindow';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'

const initalStateColors =[
  {id:1, name: "write",style:"#fff"},
  {id:2, name: "purple",style:"purple"},
  {id:3, name: "pink",style:"pink"},
  {id:4, name: "violet",style:"violet"},
  {id:5, name: "red",style:"red"},
  {id:6, name: "gold",style:"gold"},
  {id:7, name: "green",style:"#013300"},
]
function App() {
  const [date,setDate] = useState(new Date());
  const [styleTime,setStyleTime] = useState("am");
  const [modal,setModal] = useState(false);
  const [location, setLocation] = useState("")
  const [stateColors, setStateColors] = useState(initalStateColors)
  const [color, setColor] = useState(localStorage.getItem('color') == null ? "write" : localStorage.getItem('color'))
  useEffect(() => {
     setInterval(() => setDate(new Date()), 1000);
     localStorage.setItem('color', color);
   }, []);
   const clickBtn = ()=>{
     setModal(true)
   }
   const clickCloseModal = ()=>{
     setModal(false)
   }
   const choiceStyleTime = (styleTime)=>{
     setStyleTime(styleTime)
   }
   const colorsSetClick = (colors)=>{
     setColor(colors)
     localStorage.setItem('color', colors);
   }
   useEffect(()=>{
     fetch('/api')
     .then(response => response.json())
     .then(response => setLocation(`${response.region}/ ${response.country}`))
   }, [])

  return(
    <div className="main-page">
    <div className="header-btn">
      <p onClick={clickBtn}>&#9776;</p>
    </div>
    <div className="realTime-box">
      <p className="time" style={{color: color}}>{styleTime == "pm" ? `${date.getHours() % 12 || 12}:${date.getMinutes()}:${date.getSeconds()}` : date.toLocaleTimeString()}</p>
    </div>
    {location !== "" ? <div className="location" style={{color:color}}><p >{location}</p></div> : <div></div>}

       <ModalWindow
         show={modal} onHide={clickCloseModal}
         timeAm={date.toLocaleTimeString()} timePm={`${date.getHours() % 12 || 12}:${date.getMinutes()}:${date.getSeconds()}`}
         choiceStyleTime={choiceStyleTime} stateColors={stateColors} color={color}
         colorsSetClick={colorsSetClick}
       />
     </div>
  )
}

export default App;
